package core.oom;

import java.util.HashMap;
import java.util.Map;

/**
 * VM Argument :
  -Xmx256m
  -XX:-UseGCOverheadLimit
  -XX:+HeapDumpOnOutOfMemoryError -verbose:gc
  -XX:+PrintGCDetails
  -XX:+PrintGCTimeStamps
  -Xloggc:gc.log
  -XX:HeapDumpPath=c:\temp

 Reference :
    http://xmlandmore.blogspot.ie/2014/01/eclipse-mat-understand-incoming-and.html
    http://xmlandmore.blogspot.ie/search?q=OQL

 */
public class OOMJavaHeapSpaceSimulator {

    private static Map<String, OOMJavaHeapSpaceSimulator> leakContainer = new HashMap<>();
    private static final String dataPrefix = "";
    private static final boolean slowExecution = false;

    public static void main(String[] args) {
        System.out.println("Start..\n");
        try {
            for (int i = 0; i < 10000000; i++) {
                String data = dataPrefix + i;
                leakContainer.put(data, new OOMJavaHeapSpaceSimulator());
                if (slowExecution) {
                    try {
                        Thread.sleep(1);
                    } catch (Exception ignore) {

                    }
                }
            }
        } catch (Throwable throwable) {
            System.out.println("OOMJavaHeapSpaceSimulator Failure --> " + throwable);
            throw throwable;
        }
        System.out.println("\n--END--");
    }
}
